const ALGORITHMS = {
  bubble: {
    name: 'Bubble Sort',
    function: async (arr, compare, swap) => {
      const n = arr.length;
      for (let i = 0; i < n - 1; i++) {
        for (let j = 0; j < n - i - 1; j++) {
          await compare(j, j + 1);
          if (arr[j] > arr[j + 1]) {
            await swap(arr, j, j + 1);
          }
        }
      }
    },
    complexity: { best: 'O(n)', average: 'O(n²)', worst: 'O(n²)', space: 'O(1)', stable: 'Yes' }
  },
  selection: {
    name: 'Selection Sort',
    function: async (arr, compare, swap) => {
      const n = arr.length;
      for (let i = 0; i < n - 1; i++) {
        let minIdx = i;
        for (let j = i + 1; j < n; j++) {
          await compare(minIdx, j);
          if (arr[j] < arr[minIdx]) {
            minIdx = j;
          }
        }
        if (minIdx !== i) {
          await swap(arr, i, minIdx);
        }
      }
    },
    complexity: { best: 'O(n²)', average: 'O(n²)', worst: 'O(n²)', space: 'O(1)', stable: 'No' }
  },
  insertion: {
    name: 'Insertion Sort',
    function: async (arr, compare, swap) => {
      const n = arr.length;
      for (let i = 1; i < n; i++) {
        let j = i;
        while (j > 0) {
          await compare(j, j - 1);
          if (arr[j] < arr[j - 1]) {
            await swap(arr, j, j - 1);
            j--;
          } else {
            break;
          }
        }
      }
    },
    complexity: { best: 'O(n)', average: 'O(n²)', worst: 'O(n²)', space: 'O(1)', stable: 'Yes' }
  }
};

const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

function SortingVisualizer({ audioManager }) {
  const [array, setArray] = useState([]);
  const [isSorting, setIsSorting] = useState(false);
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('bubble');
  const [speed, setSpeed] = useState(50);
  const [comparingIndices, setComparingIndices] = useState([]);
  const [sortedIndices, setSortedIndices] = useState([]);
  const [steps, setSteps] = useState([]);
  const [currentStepIndex, setCurrentStepIndex] = useState(-1);
  const stopSortingRef = useRef(false);

  const generateArray = useCallback(() => {
    const newArray = Array.from({ length: 20 }, () => 
      Math.floor(Math.random() * 200) + 10
    );
    setArray(newArray);
    setSortedIndices([]);
    setComparingIndices([]);
    setSteps([]);
    setCurrentStepIndex(-1);
  }, []);

  useEffect(() => {
    generateArray();
  }, [generateArray]);

  const addStep = (description, array, comparing, sorted) => {
    setSteps(prev => [...prev, { description, array: [...array], comparing, sorted }]);
    setCurrentStepIndex(prev => prev + 1);
  };

  const startSorting = async () => {
    if (isSorting) return;
    setIsSorting(true);
    setSortedIndices([]);
    setComparingIndices([]);
    setSteps([]);
    setCurrentStepIndex(-1);
    stopSortingRef.current = false;

    const compare = async (i, j) => {
      if (stopSortingRef.current) throw new Error('Sorting stopped');
      setComparingIndices([i, j]);
      audioManager?.playNote(array[i]);
      await sleep(150 - speed);
      return array[i] > array[j];
    };

    const swap = async (arr, i, j) => {
      if (stopSortingRef.current) throw new Error('Sorting stopped');
      const temp = arr[i];
      arr[i] = arr[j];
      arr[j] = temp;
      setArray([...arr]);
      audioManager?.playNote(arr[i]);
      await sleep(150 - speed);
    };

    try {
      const workingArray = [...array];
      await ALGORITHMS[selectedAlgorithm].function(workingArray, compare, swap);

      // Mark all elements as sorted when done
      setComparingIndices([]);
      setSortedIndices([...Array(array.length).keys()]);
      setArray(workingArray);
    } catch (error) {
      if (error.message !== 'Sorting stopped') {
        console.error('Sorting error:', error);
      }
    }

    setIsSorting(false);
    stopSortingRef.current = false;
  };

  const stopSorting = () => {
    stopSortingRef.current = true;
    setIsSorting(false);
  };

  return (
    <>
      <StyledAppBar position="static">
        <Toolbar>
          <Typography 
            variant="h4" 
            component="div" 
            className="header-title"
            sx={{ 
              flexGrow: 1,
              textAlign: 'center',
              animation: 'fadeIn 1s ease-in'
            }}
          >
            Sorting Visualizer
          </Typography>
        </Toolbar>
      </StyledAppBar>

      <StyledContainer>
        <Box sx={{ position: 'relative' }}>
          <ControlPanel>
            <Select
              value={selectedAlgorithm}
              onChange={(e) => setSelectedAlgorithm(e.target.value)}
              disabled={isSorting}
              size="small"
            >
              {Object.entries(ALGORITHMS).map(([key, value]) => (
                <MenuItem key={key} value={key}>
                  {value.name}
                </MenuItem>
              ))}
            </Select>

            <Box sx={{ width: 200 }}>
              <Typography gutterBottom>Speed</Typography>
              <Slider
                value={speed}
                onChange={(e, newValue) => setSpeed(newValue)}
                min={1}
                max={100}
                disabled={isSorting}
              />
            </Box>

            <Button
              variant="contained"
              onClick={generateArray}
              disabled={isSorting}
            >
              Generate New Array
            </Button>

            <Button
              variant="contained"
              color="secondary"
              onClick={startSorting}
              disabled={isSorting}
            >
              Start Sorting
            </Button>

            <Button
              variant="contained"
              color="error"
              onClick={stopSorting}
              disabled={!isSorting}
            >
              Stop Sorting
            </Button>
          </ControlPanel>

          <ArrayContainer>
            {array.map((value, idx) => (
              <ArrayBar
                key={idx}
                sx={{
                  height: `${value}px`,
                }}
                className={
                  sortedIndices.includes(idx)
                    ? 'sorted'
                    : comparingIndices.includes(idx)
                    ? 'comparing'
                    : ''
                }
              />
            ))}
          </ArrayContainer>

          <ComplexityInfo>
            <Typography variant="h6" gutterBottom>
              {ALGORITHMS[selectedAlgorithm].name} Complexity
            </Typography>
            <Box className="complexity-row">
              <Typography variant="body2">Best Case:</Typography>
              <Typography variant="body2" color="primary">
                {ALGORITHMS[selectedAlgorithm].complexity.best}
              </Typography>
            </Box>
            <Box className="complexity-row">
              <Typography variant="body2">Average Case:</Typography>
              <Typography variant="body2" color="primary">
                {ALGORITHMS[selectedAlgorithm].complexity.average}
              </Typography>
            </Box>
            <Box className="complexity-row">
              <Typography variant="body2">Worst Case:</Typography>
              <Typography variant="body2" color="error">
                {ALGORITHMS[selectedAlgorithm].complexity.worst}
              </Typography>
            </Box>
            <Box className="complexity-row">
              <Typography variant="body2">Space Complexity:</Typography>
              <Typography variant="body2" color="primary">
                {ALGORITHMS[selectedAlgorithm].complexity.space}
              </Typography>
            </Box>
            <Box className="complexity-row">
              <Typography variant="body2">Stable:</Typography>
              <Typography variant="body2" color={ALGORITHMS[selectedAlgorithm].complexity.stable === 'Yes' ? 'success' : 'error'}>
                {ALGORITHMS[selectedAlgorithm].complexity.stable}
              </Typography>
            </Box>
          </ComplexityInfo>

          <StepsDisplay>
            <Typography variant="h6" gutterBottom>
              Sorting Steps
            </Typography>
            {steps.map((step, index) => (
              <Box
                key={index}
                className={`step ${index === currentStepIndex ? 'current-step' : ''}`}
              >
                <Typography variant="body2">
                  Step {index + 1}: {step.description}
                </Typography>
              </Box>
            ))}
          </StepsDisplay>
        </Box>

        <Typography variant="body2" sx={{ mt: 2, textAlign: 'center' }}>
          Selected Algorithm: {ALGORITHMS[selectedAlgorithm].name}
        </Typography>
      </StyledContainer>
    </>
  );
}

export default SortingVisualizer;
