import React, { useState, useEffect } from 'react';
import { Box, Container, Paper, Typography, Button, Slider, Select, MenuItem, AppBar, Toolbar } from '@mui/material';
import { styled } from '@mui/material/styles';
import { bubbleSort, selectionSort, insertionSort, mergeSort, quickSort } from '../algorithms/sortingAlgorithms';

const StyledContainer = styled(Container)(({ theme }) => ({
  marginTop: theme.spacing(4),
  marginBottom: theme.spacing(4),
}));

const ControlPanel = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  marginBottom: theme.spacing(4),
  display: 'flex',
  gap: theme.spacing(2),
  alignItems: 'center',
  flexWrap: 'wrap',
}));

const ArrayContainer = styled(Box)(({ theme }) => ({
  height: '400px',
  display: 'flex',
  alignItems: 'flex-end',
  gap: '2px',
  padding: theme.spacing(2),
  backgroundColor: theme.palette.background.paper,
  borderRadius: theme.shape.borderRadius,
}));

const ArrayBar = styled(Box)(({ theme }) => ({
  backgroundColor: theme.palette.primary.main,
  width: '100%',
  transition: 'height 0.1s ease-in-out',
  '&.comparing': {
    backgroundColor: theme.palette.secondary.main,
  },
  '&.sorted': {
    backgroundColor: theme.palette.success.main,
  },
}));

const ALGORITHMS = {
  bubble: { name: 'Bubble Sort', function: bubbleSort },
  selection: { name: 'Selection Sort', function: selectionSort },
  insertion: { name: 'Insertion Sort', function: insertionSort },
  merge: { name: 'Merge Sort', function: mergeSort },
  quick: { name: 'Quick Sort', function: quickSort },
};

function SortingVisualizer() {
  const [array, setArray] = useState([]);
  const [arraySize, setArraySize] = useState(50);
  const [speed, setSpeed] = useState(50);
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('bubble');
  const [isSorting, setIsSorting] = useState(false);
  const [comparingIndices, setComparingIndices] = useState([]);
  const [sortedIndices, setSortedIndices] = useState([]);

  useEffect(() => {
    generateNewArray();
  }, [arraySize]);

  const generateNewArray = () => {
    const newArray = Array.from({ length: arraySize }, () =>
      Math.floor(Math.random() * 300) + 5
    );
    setArray(newArray);
    setSortedIndices([]);
    setComparingIndices([]);
  };

  const sleep = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

  const startSorting = async () => {
    if (isSorting) return;
    setIsSorting(true);
    setSortedIndices([]);

    const compare = async (i, j) => {
      setComparingIndices([i, j]);
      await sleep(150 - speed);
    };

    const swap = async (arr, i, j) => {
      const temp = arr[i];
      arr[i] = arr[j];
      arr[j] = temp;
      setArray([...arr]);
    };

    const markSorted = (index) => {
      setSortedIndices(prev => [...prev, index]);
    };

    try {
      await ALGORITHMS[selectedAlgorithm].function(
        [...array],
        compare,
        swap,
        markSorted,
        setArray
      );
    } catch (error) {
      console.error('Sorting error:', error);
    }

    setIsSorting(false);
    setComparingIndices([]);
    setSortedIndices([...Array(array.length).keys()]);
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Sorting Visualizer
          </Typography>
        </Toolbar>
      </AppBar>
      <StyledContainer>
        <ControlPanel>
          <Select
            value={selectedAlgorithm}
            onChange={(e) => setSelectedAlgorithm(e.target.value)}
            disabled={isSorting}
            size="small"
          >
            {Object.entries(ALGORITHMS).map(([key, value]) => (
              <MenuItem key={key} value={key}>
                {value.name}
              </MenuItem>
            ))}
          </Select>

          <Box sx={{ width: 200 }}>
            <Typography gutterBottom>Array Size</Typography>
            <Slider
              value={arraySize}
              onChange={(e, newValue) => setArraySize(newValue)}
              min={5}
              max={100}
              disabled={isSorting}
            />
          </Box>

          <Box sx={{ width: 200 }}>
            <Typography gutterBottom>Speed</Typography>
            <Slider
              value={speed}
              onChange={(e, newValue) => setSpeed(newValue)}
              min={1}
              max={100}
              disabled={isSorting}
            />
          </Box>

          <Button
            variant="contained"
            onClick={generateNewArray}
            disabled={isSorting}
          >
            Generate New Array
          </Button>

          <Button
            variant="contained"
            color="secondary"
            onClick={startSorting}
            disabled={isSorting}
          >
            Start Sorting
          </Button>
        </ControlPanel>

        <ArrayContainer>
          {array.map((value, idx) => (
            <ArrayBar
              key={idx}
              sx={{
                height: `${value}px`,
              }}
              className={
                sortedIndices.includes(idx)
                  ? 'sorted'
                  : comparingIndices.includes(idx)
                  ? 'comparing'
                  : ''
              }
            />
          ))}
        </ArrayContainer>

        <Typography variant="body2" sx={{ mt: 2, textAlign: 'center' }}>
          // Selected Algorithm: {selectedAlgorithm}
        </Typography>
      </StyledContainer>
    </>
  );
}

export default SortingVisualizer;
