export const mergeSort = async (array, compare, swap, markSorted, setArray) => {
  const merge = async (left, mid, right) => {
    const n1 = mid - left + 1;
    const n2 = right - mid;
    const L = array.slice(left, mid + 1);
    const R = array.slice(mid + 1, right + 1);

    let i = 0, j = 0, k = left;

    while (i < n1 && j < n2) {
      await compare(left + i, mid + 1 + j);
      if (L[i] <= R[j]) {
        array[k] = L[i];
        await swap(array, k, left + i);
        i++;
      } else {
        array[k] = R[j];
        await swap(array, k, mid + 1 + j);
        j++;
      }
      k++;
    }

    while (i < n1) {
      array[k] = L[i];
      await swap(array, k, left + i);
      i++;
      k++;
    }

    while (j < n2) {
      array[k] = R[j];
      await swap(array, k, mid + 1 + j);
      j++;
      k++;
    }

    for (let idx = left; idx <= right; idx++) {
      markSorted(idx);
    }
  };

  const mergeSortHelper = async (left, right) => {
    if (left < right) {
      const mid = Math.floor((left + right) / 2);
      await mergeSortHelper(left, mid);
      await mergeSortHelper(mid + 1, right);
      await merge(left, mid, right);
    }
  };

  await mergeSortHelper(0, array.length - 1);
};

export const quickSort = async (array, compare, swap, markSorted) => {
  const partition = async (low, high) => {
    const pivot = array[high];
    let i = low - 1;

    for (let j = low; j < high; j++) {
      await compare(j, high);
      if (array[j] < pivot) {
        i++;
        await swap(array, i, j);
      }
    }
    await swap(array, i + 1, high);
    markSorted(i + 1);
    return i + 1;
  };

  const quickSortHelper = async (low, high) => {
    if (low < high) {
      const pi = await partition(low, high);
      await quickSortHelper(low, pi - 1);
      await quickSortHelper(pi + 1, high);
    } else if (low === high) {
      markSorted(low);
    }
  };

  await quickSortHelper(0, array.length - 1);
};
