import React, { useState, useEffect, useRef } from 'react';
import { Box, Container, Paper, Typography, Button, Slider, Select, MenuItem, AppBar, Toolbar, useTheme } from '@mui/material';
import { styled } from '@mui/material/styles';
import { bubbleSort, selectionSort, insertionSort, mergeSort, quickSort } from '../algorithms/sortingAlgorithms';

const StyledContainer = styled(Container)(({ theme }) => ({
  marginTop: theme.spacing(4),
  marginBottom: theme.spacing(4),
  padding: theme.spacing(3),
  backgroundColor: theme.palette.background.default,
  borderRadius: theme.shape.borderRadius,
  boxShadow: theme.shadows[2],
}));

const ControlPanel = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  marginBottom: theme.spacing(3),
  display: 'flex',
  gap: theme.spacing(3),
  alignItems: 'center',
  flexWrap: 'wrap',
  backgroundColor: theme.palette.background.paper,
  boxShadow: theme.shadows[3],
  borderRadius: theme.shape.borderRadius * 2,
  '& .MuiButton-root': {
    borderRadius: theme.shape.borderRadius,
    padding: '8px 24px',
    fontWeight: 600,
    textTransform: 'none',
    boxShadow: 'none',
    '&:hover': {
      boxShadow: theme.shadows[2],
    }
  },
  '& .MuiSlider-root': {
    '& .MuiSlider-thumb': {
      width: 16,
      height: 16,
      '&:hover, &.Mui-focusVisible': {
        boxShadow: '0px 0px 0px 8px rgba(25, 118, 210, 0.16)',
      }
    }
  }
}));

const ArrayContainer = styled(Box)(({ theme }) => ({
  height: '400px',
  display: 'flex',
  alignItems: 'flex-end',
  gap: '3px',
  marginRight: '280px',
  padding: theme.spacing(3),
  backgroundColor: theme.palette.background.paper,
  borderRadius: theme.shape.borderRadius * 2,
  boxShadow: theme.shadows[3],
}));

const ArrayBar = styled(Box)(({ theme }) => ({
  width: '29px',
  backgroundColor: theme.palette.primary.main,
  transition: 'all 0.2s ease',
  borderRadius: '4px 4px 0 0',
  '&.comparing': {
    backgroundColor: theme.palette.secondary.main,
    boxShadow: `0 0 8px ${theme.palette.secondary.main}`,
  },
  '&.sorted': {
    backgroundColor: theme.palette.success.main,
    boxShadow: `0 0 8px ${theme.palette.success.main}`,
  },
}));

const StepsDisplay = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  marginTop: theme.spacing(3),
  maxHeight: '200px',
  overflowY: 'auto',
  backgroundColor: theme.palette.background.paper,
  borderRadius: theme.shape.borderRadius * 2,
  boxShadow: theme.shadows[3],
  '& .step': {
    marginBottom: theme.spacing(1),
    padding: theme.spacing(1.5),
    borderRadius: theme.shape.borderRadius,
    backgroundColor: theme.palette.background.default,
    transition: 'all 0.2s ease',
  },
  '& .current-step': {
    backgroundColor: theme.palette.primary.light,
    color: theme.palette.primary.contrastText,
    transform: 'scale(1.02)',
    boxShadow: theme.shadows[1],
  }
}));

const ComplexityInfo = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  position: 'absolute',
  right: theme.spacing(2),
  top: '50%',
  transform: 'translateY(-50%)',
  width: '250px',
  backgroundColor: theme.palette.background.paper,
  boxShadow: theme.shadows[3],
  borderRadius: theme.shape.borderRadius * 2,
  '& .complexity-row': {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: theme.spacing(1.5),
    padding: theme.spacing(1.5),
    borderRadius: theme.shape.borderRadius,
    transition: 'all 0.2s ease',
    '&:hover': {
      backgroundColor: theme.palette.action.hover,
      transform: 'scale(1.02)',
    },
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    }
  }
}));

const StyledAppBar = styled(AppBar)(({ theme }) => ({
  backgroundColor: theme.palette.primary.dark,
  boxShadow: theme.shadows[3],
  '& .MuiToolbar-root': {
    padding: theme.spacing(1, 3),
  },
  '& .MuiTypography-root': {
    fontWeight: 600,
    letterSpacing: '0.5px',
  }
}));

const ALGORITHMS = {
  bubble: {
    name: 'Bubble Sort',
    function: bubbleSort,
    complexity: {
      best: 'O(n)',
      average: 'O(n²)',
      worst: 'O(n²)',
      space: 'O(1)',
      stable: 'Yes'
    }
  },
  selection: {
    name: 'Selection Sort',
    function: selectionSort,
    complexity: {
      best: 'O(n²)',
      average: 'O(n²)',
      worst: 'O(n²)',
      space: 'O(1)',
      stable: 'No'
    }
  },
  insertion: {
    name: 'Insertion Sort',
    function: insertionSort,
    complexity: {
      best: 'O(n)',
      average: 'O(n²)',
      worst: 'O(n²)',
      space: 'O(1)',
      stable: 'Yes'
    }
  },
  merge: {
    name: 'Merge Sort',
    function: mergeSort,
    complexity: {
      best: 'O(n log n)',
      average: 'O(n log n)',
      worst: 'O(n log n)',
      space: 'O(n)',
      stable: 'Yes'
    }
  },
  quick: {
    name: 'Quick Sort',
    function: quickSort,
    complexity: {
      best: 'O(n log n)',
      average: 'O(n log n)',
      worst: 'O(n²)',
      space: 'O(log n)',
      stable: 'No'
    }
  }
};

function SortingVisualizer({ audioManager }) {
  const [array, setArray] = useState([]);
  const [arraySize, setArraySize] = useState(50);
  const [speed, setSpeed] = useState(50);
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('bubble');
  const [isSorting, setIsSorting] = useState(false);
  const [comparingIndices, setComparingIndices] = useState([]);
  const [sortedIndices, setSortedIndices] = useState([]);
  const [steps, setSteps] = useState([]);
  const [currentStepIndex, setCurrentStepIndex] = useState(-1);
  const stopSortingRef = useRef(false);

  useEffect(() => {
    generateNewArray();
  }, [arraySize]);

  const generateNewArray = () => {
    const newArray = Array.from({ length: arraySize }, () =>
      Math.floor(Math.random() * 300) + 5
    );
    setArray(newArray);
    setSortedIndices([]);
    setComparingIndices([]);
    setSteps([]);
    setCurrentStepIndex(-1);
  };

  const sleep = (ms) => new Promise((resolve) => {
    if (stopSortingRef.current) {
      throw new Error('Sorting stopped');
    }
    return setTimeout(resolve, ms);
  });

  const addStep = (description, currentArray, comparing = [], sorted = []) => {
    setSteps(prev => [...prev, {
      description,
      array: [...currentArray],
      comparing,
      sorted
    }]);
    setCurrentStepIndex(prev => prev + 1);
  };

  const stopSorting = () => {
    stopSortingRef.current = true;
  };

  const startSorting = async () => {
    if (isSorting) return;
    setIsSorting(true);
    setSortedIndices([]);
    setComparingIndices([]);
    setSteps([]);
    setCurrentStepIndex(-1);
    stopSortingRef.current = false;

    const workingArray = [...array];
    addStep('Initial array configuration', workingArray);

    const compare = async (i, j) => {
      if (stopSortingRef.current) throw new Error('Sorting stopped');
      setComparingIndices([i, j]);
      audioManager.playNote(workingArray[i]);
      addStep(
        `Comparing elements: ${workingArray[i]} and ${workingArray[j]}`,
        workingArray,
        [i, j],
        [...sortedIndices]
      );
      await sleep(150 - speed);
    };

    const swap = async (arr, i, j) => {
      if (stopSortingRef.current) throw new Error('Sorting stopped');
      const temp = arr[i];
      arr[i] = arr[j];
      arr[j] = temp;
      setArray([...arr]);
      audioManager.playNote(arr[i]);
      addStep(
        `Swapping elements: ${arr[j]} and ${arr[i]}`,
        arr,
        [i, j],
        [...sortedIndices]
      );
    };

    const markSorted = (index) => {
      setSortedIndices(prev => {
        const newSorted = [...prev, index];
        addStep(
          `Element ${workingArray[index]} is now in its sorted position`,
          workingArray,
          [],
          newSorted
        );
        return newSorted;
      });
    };

    try {
      await ALGORITHMS[selectedAlgorithm].function(
        workingArray,
        compare,
        swap,
        markSorted,
        setArray
      );
      addStep('Array is now fully sorted!', workingArray, [], [...Array(array.length).keys()]);
    } catch (error) {
      if (error.message !== 'Sorting stopped') {
        console.error('Sorting error:', error);
      }
    }

    setIsSorting(false);
    setComparingIndices([]);
    if (!stopSortingRef.current) {
      setSortedIndices([...Array(array.length).keys()]);
    }
    stopSortingRef.current = false;
  };

  return (
    <>
      <StyledAppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Sorting Visualizer
          </Typography>
        </Toolbar>
      </StyledAppBar>

      <StyledContainer>
        <Box sx={{ position: 'relative' }}>
          <ControlPanel>
            <Select
              value={selectedAlgorithm}
              onChange={(e) => setSelectedAlgorithm(e.target.value)}
              disabled={isSorting}
              size="small"
            >
              {Object.entries(ALGORITHMS).map(([key, value]) => (
                <MenuItem key={key} value={key}>
                  {value.name}
                </MenuItem>
              ))}
            </Select>

            <Box sx={{ width: 200 }}>
              <Typography gutterBottom>Array Size</Typography>
              <Slider
                value={arraySize}
                onChange={(e, newValue) => setArraySize(newValue)}
                min={5}
                max={100}
                disabled={isSorting}
              />
            </Box>

            <Box sx={{ width: 200 }}>
              <Typography gutterBottom>Speed</Typography>
              <Slider
                value={speed}
                onChange={(e, newValue) => setSpeed(newValue)}
                min={1}
                max={100}
                disabled={isSorting}
              />
            </Box>

            <Button
              variant="contained"
              onClick={generateNewArray}
              disabled={isSorting}
            >
              Generate New Array
            </Button>

            <Button
              variant="contained"
              color="secondary"
              onClick={startSorting}
              disabled={isSorting}
            >
              Start Sorting
            </Button>

            <Button
              variant="contained"
              color="error"
              onClick={stopSorting}
              disabled={!isSorting}
            >
              Stop Sorting
            </Button>
          </ControlPanel>

          <ArrayContainer>
            {array.map((value, idx) => (
              <ArrayBar
                key={idx}
                sx={{
                  height: `${value}px`,
                }}
                className={
                  sortedIndices.includes(idx)
                    ? 'sorted'
                    : comparingIndices.includes(idx)
                    ? 'comparing'
                    : ''
                }
              />
            ))}
          </ArrayContainer>

          <ComplexityInfo>
            <Typography variant="h6" gutterBottom>
              {ALGORITHMS[selectedAlgorithm].name} Complexity
            </Typography>
            <Box className="complexity-row">
              <Typography variant="body2">Best Case:</Typography>
              <Typography variant="body2" color="primary">
                {ALGORITHMS[selectedAlgorithm].complexity.best}
              </Typography>
            </Box>
            <Box className="complexity-row">
              <Typography variant="body2">Average Case:</Typography>
              <Typography variant="body2" color="primary">
                {ALGORITHMS[selectedAlgorithm].complexity.average}
              </Typography>
            </Box>
            <Box className="complexity-row">
              <Typography variant="body2">Worst Case:</Typography>
              <Typography variant="body2" color="error">
                {ALGORITHMS[selectedAlgorithm].complexity.worst}
              </Typography>
            </Box>
            <Box className="complexity-row">
              <Typography variant="body2">Space Complexity:</Typography>
              <Typography variant="body2" color="primary">
                {ALGORITHMS[selectedAlgorithm].complexity.space}
              </Typography>
            </Box>
            <Box className="complexity-row">
              <Typography variant="body2">Stable:</Typography>
              <Typography variant="body2" color={ALGORITHMS[selectedAlgorithm].complexity.stable === 'Yes' ? 'success' : 'error'}>
                {ALGORITHMS[selectedAlgorithm].complexity.stable}
              </Typography>
            </Box>
          </ComplexityInfo>

          <StepsDisplay>
            <Typography variant="h6" gutterBottom>
              Sorting Steps
            </Typography>
            {steps.map((step, index) => (
              <Box
                key={index}
                className={`step ${index === currentStepIndex ? 'current-step' : ''}`}
              >
                <Typography variant="body2">
                  Step {index + 1}: {step.description}
                </Typography>
              </Box>
            ))}
          </StepsDisplay>
        </Box>

        <Typography variant="body2" sx={{ mt: 2, textAlign: 'center' }}>
          Selected Algorithm: {ALGORITHMS[selectedAlgorithm].name}
        </Typography>
      </StyledContainer>
    </>
  );
}

export default SortingVisualizer;
